  const idx = getModelFilterIdx(_mf);
  if (idx !== -1) {
    _modelFilters.data.splice(idx, 1);
  }
}

function removeModelFilters(_mfs: CarModelDetailsFilter[]) {
  for (const _mf of _mfs) {
    removeModelFilter(_mf);
  }
