  updateDoc,
} from "@firebase/firestore";
import { firebaseStore } from "@/lib/firebase";
import useChat from "./useChat";
import { v4 as uuid } from "uuid";
import { uploadImg } from "@/services/firebase";
import { Message } from "@/model/Chats";

const { getAuthUser } = useAuthUser();
const currentUser = getAuthUser;

const { chatState } = useChat();
const messages: Ref<Message[]> = ref([]);

async function sendImgMessage(text, file) {
  const imgUrl = await uploadImg(file);

  await updateDoc(doc(firebaseStore, "chats", chatState.chatId), {
    messages: arrayUnion({
      id: uuid(),
      text,
      senderId: currentUser.value.uid,
      date: Timestamp.now(),
      img: imgUrl,
    }),
  });
}

async function sendMessage(text) {
  await updateDoc(doc(firebaseStore, "chats", chatState.chatId), {
    messages: arrayUnion({
      id: uuid(),
      text,
      senderId: currentUser.value.uid,
      date: Timestamp.now(),
    }),
  });
  await updateUserChats(text, currentUser.value.uid);
}

async function updateUserChats(text, senderId) {
  await updateDoc(doc(firebaseStore, "userChats", currentUser.value.uid), {
    [chatState.chatId + ".lastMessage"]: { text, senderId },
    [chatState.chatId + ".date"]: serverTimestamp(),
  });

  await updateDoc(doc(firebaseStore, "userChats", chatState.user.uid), {
    [chatState.chatId + ".lastMessage"]: { text, senderId },
    [chatState.chatId + ".date"]: serverTimestamp(),
  });
}

export default function useMessage() {
  onSnapshot(doc(firebaseStore, "chats", chatState.chatId), (doc) => {
    console.log(doc.data());
    if (doc.exists()) {
      messages.value = doc.data().messages;
    }
  });

  return {
    messages,
    sendImgMessage,
    sendMessage,
  };
}

import { ref, watch, computed, reactive, Ref, compile } from "vue";
import useAuthUser from "../user/useAuthUser";

import { doc, onSnapshot } from "@firebase/firestore";
import { firebaseStore } from "@/lib/firebase";

import { getUserChatsByTargetID } from "@/services/firebase";

const { getAuthUser } = useAuthUser();
const currentUser = getAuthUser;

const chatState = reactive({ chatId: "null", user: { uid: "" } });

const userChats: Ref<[string, any][]> = ref([]);

function filterUserChats(searchStr) {
  return userChats.value.filter((ele) =>
    ele[1].userInfo.displayName.includes(searchStr)
  );
}

async function setChatStateByUserId(uid) {
  const payload = await getUserChatsByTargetID(getAuthUser.value.uid, uid);
  console.log(payload);
  setChatState(payload[1].userInfo);
}

function setChatState(payload) {
  chatState.chatId =
    currentUser.value.uid > payload.uid
      ? currentUser.value.uid + payload.uid
      : payload.uid + currentUser.value.uid;
  chatState.user = payload;
}

export default function useChat() {
  return {
    chatState,
    userChats,
    setChatState,
    filterUserChats,
    setChatStateByUserId,
  };
}

import { ref, watch, computed, reactive } from "vue";
// import {  } from "../services/firebase"
import {
  IModelFilter,
  CarModelFilter,
  ModelSubCategoryFilter,
} from "@/model/SearchCar/modelFilter";

import useSearchFilter from "@/hooks/search/useSearchFilter";

const { getCateFilter } = useSearchFilter();

const _modelFilters = reactive<{
  data: IModelFilter[];
}>({ data: [] });

function getModelFilters() {
  return _modelFilters.data;
}

function getModelFilterIdx(_mf: IModelFilter) {
  return _modelFilters.data.findIndex((ele) => ele.modelType === _mf.modelType);
}

function getModelFilterByModelType(modelType) {
  return ref(
    (_modelFilters.data.find(
      (ele) => ele.modelType === modelType
    ) as CarModelFilter) || new CarModelFilter(modelType)
  );
}

function addModelFilter(_mf: IModelFilter) {
  const idx = getModelFilterIdx(_mf);
  if (idx === -1) {
    _modelFilters.data.push(_mf);
  }
}

function removeModelFilter(_mf: IModelFilter) {
  const idx = getModelFilterIdx(_mf);
  if (idx !== -1) {
    _modelFilters.data.splice(idx, 1);
  }
}

//not yet review

function addModelSubCatFilter(modelType, value: ModelSubCategoryFilter) {
  const _mf = getModelFilterByModelType(modelType).value;
  _mf.addSubCatFilter(value);
}

function removeModelSubCatFilter(modelType, value: ModelSubCategoryFilter) {
  const _mf = getModelFilterByModelType(modelType).value;
  const idx = getModelFilterIdx(_mf);
  _mf.removeSubCatFilter(value);
}

function getModelSubCatFilter(modelType, subCategory) {
  const _mf = getModelFilterByModelType(modelType).value;
  return _mf.getModelSubCatFilter(subCategory);
}

function resetmodelFilters() {
  _modelFilters.data = [];
}

function setModelFilters(modelFilter) {
  _modelFilters.data = modelFilter;
}

export default function useModelFilter() {
  return {
    getModelFilters,

    getModelFilterByModelType,
    addModelFilter,
    removeModelFilter,
    addModelSubCatFilter,
    removeModelSubCatFilter,
    getModelSubCatFilter,
    resetmodelFilters,
    setModelFilters,
  };
}
